mapper.update:
UPDATE SECONDHAND_PRODUCT
SET SP_TITLE = #{spTitle}, SP_PRICE = #{spPrice}, SP_CONTENT = #{spContent}, SP_ADDRESS = #{spAddress}, CATEGORY_NO = #{categoryNo}
where member_id = #{memberId} and SP_STATUS = 'N' and sp_no = #{spNo}

update SECONDHAND_PRODUCT
SET SP_IS_BOOK='Y'
where SP_NO = #{spNo}

update SECONDHAND_PRODUCT
SET SP_IS_BOOK='N'
where SP_NO = #{spNo}

update SECONDHAND_PRODUCT
SET SP_IS_SELL='Y'
where SP_NO = #{spNo}

update SECONDHAND_PRODUCT
SET SP_STATUS='Y'
where SP_NO = #{spNo}

mapper.delete:
delete from photo
where photo_rename in 

	#{item}

mapper.update:
update photo
set Photo_status = 'N'
where PHOTO_NO = #{spNo}

update SECONDHAND_PRODUCT
set sp_count = sp_count + 1
where sp_no = #{spNo}
